import { API } from 'constants/index';

export const fetchArticles = (limit = 10, offset) => async (dispatch) => {

    dispatch({
        type: 'FETCH_ARTICLES_REQUSET',
    });

    try {
        const url = `${API}/articles?limit=${limit}&offset=${offset ? offset * limit : 0}`;
        const response = await fetch(url);
        const result = await response.json();

        dispatch({
            type: 'FETCH_ARTICLES_SUCCESS',
            payload: result,
        });
    } catch (error) {
        dispatch({
            type: 'FETCH_ARTICLES_FAILURE',
            payload: error,
        });
    }
};

