import { API } from 'constants/index';

export const registerUser = (username, email, password) => async (dispatch) => {

    dispatch({
        type: 'POST_REGISTER_REQUEST',
    });

    try {
        const url = `${API}/users`;
        const options = {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json;charset=utf-8',
            },
            body: JSON.stringify({ user: { username, email, password } }),
        };
        const response = await fetch(url, options);
        const result = await response.json();
        console.log(result);

        localStorage.setItem('jwt', result.user.token);

        dispatch({
            type: 'POST_REGISTER_SUCCESS',
            payload: result.user,
        });
    } catch (error) {
        dispatch({
            type: 'POST_REGISTER_FAILURE',
            payload: error,
        });
    }

};

export const userLoginFetch = (email, password) => async (dispatch) => {

    dispatch({
        type: 'POST_LOGIN_REQUEST',
    });

    try {
        const url = `${API}/users/login`;
        const options = {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json;charset=utf-8',
            },
            body: JSON.stringify({ user: { email, password } }),
        };
        const response = await fetch(url, options);
        const result = await response.json();

        localStorage.setItem('jwt', result.user.token);

        dispatch({
            type: 'POST_LOGIN_SUCCESS',
            payload: result.user,
        });
    } catch (error) {
        dispatch({
            type: 'POST_LOGIN_FAILURE',
            payload: error,
        });
    }
};

export const getProfileFetch = () => async (dispatch) => {

    const token = localStorage.getItem('jwt');

    if (token) {

        dispatch({
            type: 'FETCH_AUTHENTICATION_REQUEST'
        });

        try {
            const url = `${API}/user`;
            const options = {
                method: 'GET',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json;charset=utf-8',
                    'Authorization': `Token ${token}`
                },
            };
            const response = await fetch(url, options);
            const result = await response.json();

            dispatch({
                type: 'FETCH_AUTHENTICATION_SUCCESS',
                payload: result.user,
            });
        } catch (error) {
            localStorage.removeItem('jwt');

            dispatch({
                type: 'FETCH_AUTHENTICATION_FAILURE',
                payload: error,
            });
        }

    }
};

export const logoutUser = () => ({
    type: 'LOGOUT_USER'
});