import Api from '../api';

export const fetchProfile = (username) => async (dispatch) => {

    dispatch({
        type: 'FETCH_PROFILE_REQUEST'
    });

    try {
        const result = await Api.Profile.get(username);

        dispatch({
            type: 'FETCH_PROFILE_SUCCESS',
            payload: result.profile,
        });
    } catch (error) {
        dispatch({
            type: 'FETCH_PROFILE_FAILURE',
            payload: error,
        });
    }
};

