import React from 'react';
import { Link } from 'react-router-dom';

import './account.scss';

function Account({ user }) {

    const { username, image } = user;

    return (
        <div className="account">
            <Link to={`/@${username}`}>
                <img src={image} alt={username} />
                {username}
            </Link>
        </div>
    );
}

export default Account;