import React, { useState } from 'react';
import { connect } from 'react-redux';
import { Button } from 'evergreen-ui';
import Api from 'api';

import './like.scss';

function Like({ favorited, favoritesCount, slug, like, unlike }) {

    const [isFetching, setIsFetching] = useState(false);

    const handleClick = (event) => {
        event.preventDefault();
        if (favorited) {
            setIsFetching(true);
            Api.Articles.unlike(slug).then((result) => {
                console.log(result);
                unlike(result);
                setIsFetching(false);
            });
        } else {
            setIsFetching(true);
            Api.Articles.like(slug).then((result) => {
                console.log(result);
                like(result);
                setIsFetching(false);
            });
        }
    };

    return (
        <Button
            appearance="primary"
            intent={!favorited ? "success" : "danger"}
            iconBefore={isFetching ? null : !favorited ? 'heart' : 'heart-broken'}
            isLoading={isFetching}
            onClick={handleClick}
        >
            <span>{favoritesCount}</span>
        </Button>
    );
}

const mapDispatchToProps = (dispatch) => ({
    like: (payload) =>
        dispatch({ type: 'FETCH_LIKE_SUCCESS', payload }),
    unlike: (payload) =>
        dispatch({ type: 'FETCH_UNLIKE_SUCCESS', payload }),
});

export default connect(null, mapDispatchToProps)(Like);