import React from 'react';
import { NavLink } from 'react-router-dom';

import './nav.scss';

const LoggedOut = [
    { id: 1, title: 'Главная', href: '/', exact: true },
];

const LoggedIn = [
    { id: 4, title: 'Главная', href: '/', exact: true },
    { id: 5, title: 'Новый пост', href: '/article/new', exact: true },
    { id: 6, title: 'Настройки', href: '/settings', exact: false },
];

function Nav({ isAuthenticated = false }) {

    const navigation = isAuthenticated ? LoggedIn : LoggedOut;

    return (
        <ul className="nav">
            {navigation.map(({ id, href, title, exact }) => (
                <li className="nav-item" key={id}>
                    <NavLink className="nav-link" to={href} exact={exact}>{title}</NavLink>
                </li>
            ))}
        </ul>
    );
}

export default Nav;
