import React from 'react';
import { Route, Redirect } from 'react-router-dom';

function PrivateRoute({ component: RouteComponent, isAuthenticated, ...rest }) {
    return (
        <Route
            {...rest}
            render={(routeProps) =>
                isAuthenticated ? (
                    <RouteComponent />
                ) : (
                        <Redirect
                            to={{
                                pathname: "/login",
                                state: { from: routeProps.location }
                            }}
                        />
                    )
            }
        />
    );
}

export default PrivateRoute;
