import React from 'react';
import PropTypes from 'prop-types';

import Author from 'components/Author';
import Like from 'components/Like';

import './articleSingle.scss';
import { Button } from 'evergreen-ui';

function ArticleSingle({ slug, title, body, author, createdAt, favorited, favoritesCount, tagList = [] }) {

    return (
        <article className="article-single">
            <header className="article-single-heading">
                <h1 className="article-single-title">{title}</h1>
                <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between'
                }}>
                    <Author
                        {...author}
                        createdAt={createdAt}
                    />
                    <Like
                        slug={slug}
                        favorited={favorited}
                        favoritesCount={favoritesCount}
                    />
                </div>
            </header>
            <p className="article-single-body">
                {body}
            </p>
            <ul className="article-single-tagList">
                {tagList.length > 0 && tagList.map((tag) => (
                    <Button key={tag}>{tag}</Button>
                ))}
            </ul>
        </article>
    );
}

ArticleSingle.propTypes = {
    tagList: PropTypes.any,
};

export default ArticleSingle;