import { FETCH_ARTICLE_FAILURE, FETCH_ARTICLE_REQUEST, FETCH_ARTICLE_SUCCESS } from 'pages/Article/actionTypes';
import { DELETE_LIKE_SUCCESS, POST_LIKE_SUCCESS } from 'pages/Home/actionTypes';

const initialState = {
    isFetching: false,
    data: null,
    errorMessage: null,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case FETCH_ARTICLE_REQUEST:
            return { ...state, isFetching: true, errorMessage: null };
        case FETCH_ARTICLE_SUCCESS:
            return { ...state, isFetching: false, data: payload.article };
        case FETCH_ARTICLE_FAILURE:
            return { ...state, isFetching: false, errorMessage: payload };

        case POST_LIKE_SUCCESS:
        case DELETE_LIKE_SUCCESS:
            return {
                ...state,
                data: {
                    ...state.article,
                    favorited: payload.article.favorited,
                    favoritesCount: payload.article.favoritesCount,
                },
            };
        default:
            return state;
    }

};