import { createSelector } from 'reselect';

const articleIsFetchingSelector = (state) => state.article.isFetching;
const articleItemsSelector = (state) => state.article.data;
const articleErrorMessage = (state) => state.article.errorMessage;

export const articleSelector = createSelector(
    articleIsFetchingSelector,
    articleItemsSelector,
    articleErrorMessage,
    (isFetching, data, errorMessage) => ({
        isFetching, data, errorMessage,
    }),
);

