import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';

import Articles from '../components/Articles';
import { fetchArticlesByAuthor } from '../actions/articles';
import { getArticlesSelector } from '../selectors';

function ArticlesByAuthorContainer({ fetchArticlesByAuthor, articles }) {

    const { username } = useParams();

    useEffect(() => {
        fetchArticlesByAuthor(username);
    }, [fetchArticlesByAuthor, username]);

    return <Articles {...articles} />;
}

const mapStateToProps = (state) => ({
    articles: getArticlesSelector(state),
});

const mapDispatchToProps = {
    fetchArticlesByAuthor,
};

export default connect(mapStateToProps, mapDispatchToProps)(ArticlesByAuthorContainer);