import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';

import Articles from '../components/Articles';
import { fetchArticlesFavorites } from '../actions/articles';
import { getArticlesSelector } from '../selectors';

function ArticlesFavoritesContainer({ fetchArticlesFavorites, articles }) {

    const { username } = useParams();

    useEffect(() => {
        fetchArticlesFavorites(username);
    }, [fetchArticlesFavorites, username]);

    return <Articles {...articles} />;
}

const mapStateToProps = (state) => ({
    articles: getArticlesSelector(state),
});

const mapDispatchToProps = {
    fetchArticlesFavorites,
};

export default connect(mapStateToProps, mapDispatchToProps)(ArticlesFavoritesContainer);