import React, { useEffect } from 'react';
import { connect } from 'react-redux';

import Articles from '../components/Articles';
import { fetchArticlesFeed } from '../actions/articles';
import { getArticlesSelector } from '../selectors';

function ArticlesFeedContainer({ fetchArticlesFeed, articles }) {

    useEffect(() => {
        fetchArticlesFeed();
    }, [fetchArticlesFeed]);

    return <Articles {...articles} />;
}

const mapStateToProps = (state) => ({
    articles: getArticlesSelector(state),
});

const mapDispatchToProps = {
    fetchArticlesFeed,
};

export default connect(mapStateToProps, mapDispatchToProps)(ArticlesFeedContainer);