import {
    POST_LIKE_REQUEST,
    POST_LIKE_SUCCESS,
    POST_LIKE_FAILURE,
    DELETE_LIKE_REQUEST,
    DELETE_LIKE_SUCCESS,
    DELETE_LIKE_FAILURE,
} from '../actionTypes';

const initialState = {
    isFetching: false,
    errorMessage: null,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case POST_LIKE_REQUEST:
        case DELETE_LIKE_REQUEST:
            return { ...state, isFetching: true, errorMessage: null };
        case POST_LIKE_SUCCESS:
        case DELETE_LIKE_SUCCESS:
            return { ...state, isFetching: false };
        case POST_LIKE_FAILURE:
        case DELETE_LIKE_FAILURE:
            return { ...state, isFetching: false, errorMessage: payload };
        default:
            return state;
    }

};