import { GET_TAGS_REQUEST, GET_TAGS_SUCCESS, GET_TAGS_FAILURE } from '../actionTypes';

const initialState = {
    isFetching: false,
    items: [],
    errorMessage: null,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case GET_TAGS_REQUEST:
            return { ...state, isFetching: true, errorMessage: null };
        case GET_TAGS_SUCCESS:
            return { ...state, isFetching: false, items: payload };
        case GET_TAGS_FAILURE:
            return { ...state, isFetching: false, errorMessage: payload };
        default:
            return state;
    }

};