import { FETCH_TAGS_FAILURE, FETCH_TAGS_REQUEST, FETCH_TAGS_SUCCESS } from '../actionTypes';

const initialState = {
    isFetching: false,
    data: [],
    errorMessage: null,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case FETCH_TAGS_REQUEST:
            return { ...state, isFetching: true, errorMessage: null };
        case FETCH_TAGS_SUCCESS:
            return { ...state, isFetching: false, data: payload };
        case FETCH_TAGS_FAILURE:
            return { ...state, isFetching: false, errorMessage: payload };
        default:
            return state;
    }

};