import { createSelector } from 'reselect';

const articlesIsFetchingSelector = (state) => state.articles.isFetching;
const articlesItemsSelector = (state) => state.articles.data;
const articlesItemsCountSelector = (state) => state.articles.articlesCount;
const articlesErrorMessage = (state) => state.articles.errorMessage;

export const articlesSelector = createSelector(
    articlesIsFetchingSelector,
    articlesItemsSelector,
    articlesItemsCountSelector,
    articlesErrorMessage,
    (isFetching, data, articlesCount, errorMessage) => ({
        isFetching, data, articlesCount, errorMessage,
    }),
);

const tagsItemsSelector = (state) => state.tags.data;
const tagsIsFetchingSelector = (state) => state.tags.isFetching;
const tagsErrorMessage = (state) => state.tags.errorMessage;

export const tagsSelector = createSelector(
    tagsIsFetchingSelector,
    tagsItemsSelector,
    tagsErrorMessage,
    (isFetching, data, errorMessage) => ({
        isFetching, data, errorMessage,
    }),
);