import { createSelector } from 'reselect';

const articlesIsFetching = (state) => state.articles.isFetching;
const articlesItems = (state) => state.articles.items;
const articlesItemsCount = (state) => state.articles.itemsCount;
const articlesErrorMessage = (state) => state.articles.errorMessage;

export const getArticlesSelector = createSelector(
    articlesIsFetching,
    articlesItems,
    articlesItemsCount,
    articlesErrorMessage,
    (isFetching, items, itemsCount, errorMessage) => ({
        isFetching, items, itemsCount, errorMessage,
    }),
);

const tagsIsFetching = (state) => state.tags.isFetching;
const tagsItems = (state) => state.tags.items;
const tagsErrorMessage = (state) => state.tags.errorMessage;

export const getTagsSelector = createSelector(
    tagsIsFetching,
    tagsItems,
    tagsErrorMessage,
    (isFetching, items, errorMessage) => ({
        isFetching, items, errorMessage,
    }),
);