import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Redirect } from 'react-router-dom';
import { Alert, Button, Checkbox, Pane, TextInput } from 'evergreen-ui';

import './login.scss';

function Login({ signIn, auth }) {

    const [email, setEmail] = useState('seryozha.baleyko@gmail.com');
    const [password, setPassword] = useState('gfhjkm1997');
    const [checked, setChecked] = useState(false);

    const handleSubmit = (event) => {
        event.preventDefault();
        signIn(email, password);
    };

    const { isFetching, errorMessage, isAuthenticated } = auth;

    if (isAuthenticated) {
        return <Redirect to="/"/>;
    }

    return (
        <main className="main">
            <div className="container">
                <div className="login">
                    <form onSubmit={handleSubmit}>
                        <header className="login-heading">
                            <h1>Войти</h1>
                            <p>Быстро и легко.</p>
                        </header>

                        {errorMessage && (
                            <Pane>
                                {Object.keys(errorMessage).map((key) => (
                                    <Alert
                                        key={key}
                                        appearance="card"
                                        intent="danger"
                                        title={`${key} ${errorMessage[key]}`}
                                    />
                                ))}
                            </Pane>
                        )}

                        <div>
                            <TextInput
                                required
                                type="email"
                                placeholder="Электронный адрес"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                width="100%"
                                height={40}
                            />
                        </div>

                        <div>
                            <TextInput
                                required
                                type="password"
                                placeholder="Пароль"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                width="100%"
                                height={40}
                            />
                        </div>

                        <div>
                            <Checkbox
                                label="Remember me"
                                checked={checked}
                                onChange={(e) => setChecked(e.target.checked)}
                            />
                        </div>

                        <Button
                            type="submit"
                            appearance="primary"
                            isLoading={isFetching}
                            height={40}
                        >
                            Войти
                        </Button>
                    </form>
                </div>
            </div>
        </main>
    );
}

Login.propTypes = {
    signIn: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
};

export default Login;