import { createSelector } from 'reselect';

const authIsFetchingSelector = (state) => state.login.isFetching;
const authErrorMessageSelector = (state) => state.login.errorMessage;
const authIsAuthenticatedSelector = (state) => state.auth.isAuthenticated;

export const getAuthSelector = createSelector(
    authIsFetchingSelector,
    authErrorMessageSelector,
    authIsAuthenticatedSelector,
    (isFetching, errorMessage, isAuthenticated) => ({
        isFetching, errorMessage, isAuthenticated,
    }),
);