import React from 'react';
import { Avatar, Button } from 'evergreen-ui';

import './User.scss';
import { Link } from 'react-router-dom';

function User({ isFetching, username, bio, image, following, auth, errorMessage }) {

    const isAuth = auth.currentUser && auth.currentUser.username === username;

    return (
        <section className="user">
            <Avatar src={image} name={username} size={150} />
            <h1>{username}</h1>
            <p>{bio}</p>
            {isAuth ? (
                <Link to={`@${auth.currentUser.username}/edit`}>Edit Profile Settings</Link>
            ) : (
                <Button>follow</Button>
            )}
        </section>
    );
}

export default User;