import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams } from 'react-router-dom';

import Index from 'pages/Home/components/Articles/Articles';
import { fetchArticlesByAuthor } from '../actions';
import { getArticlesSelector } from '../selector';

function ArticlesContainer({ fetchArticlesByAuthor, articles }) {

    const { username } = useParams();

    useEffect(() => {
        fetchArticlesByAuthor(username);
    }, [fetchArticlesByAuthor, username]);

    return <Index {...articles}/>;
}

const mapStateToProps = (state) => ({
    articles: getArticlesSelector(state),
});

const mapDispatchToProps = {
    fetchArticlesByAuthor,
};

export default connect(mapStateToProps, mapDispatchToProps)(ArticlesContainer);