import { GET_PROFILE_FAILURE, GET_PROFILE_REQUEST, GET_PROFILE_SUCCESS } from './actionTypes';

const initialState = {
    isFetching: false,
    username: '',
    bio: '',
    image: null,
    following: null,
    errorMessage: null,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case GET_PROFILE_REQUEST:
            return { ...state, isFetching: true, errorMessage: null };
        case GET_PROFILE_SUCCESS:
            return { ...state, isFetching: false, ...payload };
        case GET_PROFILE_FAILURE:
            return { ...state, isFetching: false, errorMessage: payload };
        default:
            return state;
    }

};