import { createSelector } from 'reselect';

const profileIsFetching = (state) => state.profile.isFetching;
const profileUsername = (state) => state.profile.username;
const profileBio = (state) => state.profile.bio;
const profileImage = (state) => state.profile.image;
const profileFollowing = (state) => state.profile.following;
const profileErrorMessage = (state) => state.profile.errorMessage;

export const getProfileSelector = createSelector(
    profileIsFetching,
    profileUsername,
    profileBio,
    profileImage,
    profileFollowing,
    profileErrorMessage,
    (isFetching, username, bio, image, following, errorMessage) => ({
        isFetching, username, bio, image, following, errorMessage,
    }),
);

const articlesIsFetching = (state) => state.articles.isFetching;
const articlesItems = (state) => state.articles.data;
const articlesErrorMessage = (state) => state.articles.errorMessage;

export const getArticlesSelector = createSelector(
    articlesIsFetching,
    articlesItems,
    articlesErrorMessage,
    (isFetching, items, errorMessage) => ({
        isFetching, items, errorMessage,
    }),
);

const authIsFetching = (state) => state.auth.isFetching;
const authCurrentUser = (state) => state.auth.currentUser;
const authIsAuthenticated = (state) => state.auth.isAuthenticated;

export const getAuthSelector = createSelector(
    authIsFetching,
    authCurrentUser,
    authIsAuthenticated,
    (isFetching, currentUser, isAuthenticated) => ({
        isFetching, currentUser, isAuthenticated,
    }),
);