import React from 'react';
import { connect } from 'react-redux';

import Layout from 'components/Layout';
import { setUsername, setEmail, setPassword, submit } from 'actions/register';

import './register.scss';

function Register({ isFetching, username, setUsername, email, setEmail, password, setPassword, submit }) {

    const handleChangeUsername = (event) => setUsername(event.target.value);
    const handleChangeEmail = (event) => setEmail(event.target.value);
    const handleChangePassword = (event) => setPassword(event.target.value);

    const handleSubmit = (event) => {
        event.preventDefault();
        submit(username, email, password);
    };

    const isInvalid = email === '' || password === '' || username === '';

    return (
        <Layout>
            <section className="register">
                <h1>Регистрация</h1>
                <p>Быстро и легко.</p>
                <form onSubmit={handleSubmit}>
                    <div>
                        <input
                            type="text"
                            placeholder="Имя пользователя"
                            value={username}
                            onChange={handleChangeUsername}
                        />
                    </div>
                    <div>
                        <input
                            type="email"
                            placeholder="Электронный адрес"
                            value={email}
                            onChange={handleChangeEmail}
                        />
                    </div>
                    <div>
                        <input
                            type="password"
                            placeholder="Пароль"
                            value={password}
                            onChange={handleChangePassword}
                        />
                    </div>
                    <div>
                        <button
                            className="button primary"
                            type="submit"
                            disabled={isInvalid || isFetching}
                        >
                            Зарегистрироваться
                        </button>
                    </div>
                </form>
            </section>
        </Layout>
    );
}

const mapStateToProps = state => ({ ...state.auth });

const mapDispatchToProps = {
    setUsername,
    setEmail,
    setPassword,
    submit,
};

export default connect(mapStateToProps, mapDispatchToProps)(Register);