const initialState = {
    isFetching: false,
    user: {
        username: null,
    },
    currentUser: null,
    errors: null,
    isAuthenticated: false,
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case 'POST_LOGIN_REQUEST':
        case 'POST_REGISTER_REQUEST':
        case 'FETCH_AUTHENTICATION_REQUEST':
            return { ...state, isFetching: true };
        case 'POST_LOGIN_SUCCESS':
        case 'POST_REGISTER_SUCCESS':
        case 'FETCH_AUTHENTICATION_SUCCESS':
            return { ...state, isFetching: false, currentUser: payload, user: payload, isAuthenticated: true };
        case 'POST_LOGIN_FAILURE':
        case 'POST_REGISTER_FAILURE':
        case 'FETCH_AUTHENTICATION_FAILURE':
            return { ...state, isFetching: false, errors: payload };

        case 'LOGOUT_USER':
            return { ...state, currentUser: {}, isAuthenticated: false }

        default:
            return state;
    }
};