import { combineReducers } from 'redux';

import feed from 'reducers/feed';
import tags from 'pages/Home/reducers/tags';
import articles from 'pages/Home/reducers/articles';

import auth from './auth';
import login from '../pages/Login/reducer';
import register from '../pages/Register/reducer';
import profile from '../pages/Profile/reducer';
import article from '../pages/Article/reducers/article';
import comments from '../pages/Article/reducers/comments';

const rootReducer = combineReducers({
    auth,
    login,
    register,
    profile,
    tags,
    articles,
    article,
    comments,
    feed,
});

export default rootReducer;