const initialState = {
    isFetching: false,
    error: null,
    username: '',
    bio: '',
    image: '',
    following: '',
};

export default (state = initialState, { type, payload }) => {

    switch (type) {
        case 'FETCH_PROFILE_REQUEST':
            return { ...state, isFetching: true };
        case 'FETCH_PROFILE_SUCCESS':
            return { ...state, isFetching: false, ...payload };
        case 'FETCH_PROFILE_FAILURE':
            return { ...state, isFetching: false, error: payload };
        case 'POST_FOLLOW_REQUEST':
        case 'DELETE_UNFOLLOW_REQUEST':
            return { ...state };
        case 'POST_FOLLOW_SUCCESS':
        case 'DELETE_UNFOLLOW_SUCCESS':
            return { ...state, ...payload };
        case 'POST_FOLLOW_FAILURE':
        case 'DELETE_UNFOLLOW_FAILURE':
            return { ...state };
        default:
            return state;
    }
};