import { createSelector } from 'reselect';

const authIsFetchingSelector = (state) => state.auth.isFetching;
const authCurrentUserSelector = (state) => state.auth.currentUser;
const authIsAuthenticatedSelector = (state) => state.auth.isAuthenticated;

export const getAuthSelector = createSelector(
    authIsFetchingSelector,
    authCurrentUserSelector,
    authIsAuthenticatedSelector,
    (isFetching, currentUser, isAuthenticated) => ({
        isFetching, currentUser, isAuthenticated,
    }),
);